INSERT INTO `gastenboek` VALUES ('8', 'De Kampert', '2003-04-12 21:43:00', 'Kemps en consorten,\r\n\r\nIk ben trots op jullie! \r\nHet ziet er echt goed uit!! Als wij morgen is net zo goed gaan hockeyen, dan rollen we Basko op!!\r\nKomt goed, tot morgen!\r\n\r\nGroeten');
INSERT INTO `gastenboek` VALUES ('9', 'Pistool', '2003-04-13 00:09:59', 'He mensen, jaja. Ziet er errug netjes uit. Het begint op www.oirschot-vooruit.nl te lijken, hihi. Even zonder dollen... veel succes morgen tegen de Blauw-witten uit Veldhoven. Misschien dak ffkus aankom. Later...\r\n\r\nGroeten uit het land van laaf <IMG SRC=\"emoticons/shades_smile.gif\">  <IMG SRC=\"emoticons/kiss.gif\"> ');
INSERT INTO `gastenboek` VALUES ('10', 'Rob', '2003-04-13 10:27:28', 'Hoihoi,\r\nziet er goed uit!!! Kun je tzt zo\'n site voor de JTC maken??? Dat zou perfect zijn!!!\r\nIk mis trouwens de weekelijkse verslagen van Tom op de site, maar misschien komen die nog. <IMG SRC=\"emoticons/beer_yum.gif\"> na de wedstrijd!');
