INSERT INTO `gastenboek` VALUES ('12', 'nr 6 (vinke)', '2003-04-13 11:54:40', 'dit is groots werk jongens ik heb geen idee hoe jullie dit flikken maar ik ben heel trots op het resultaat. nu basko nog vanmiddag even overwinnen en dan een lekker  <IMG SRC=\"emoticons/beer_yum.gif\"> of misschien wel  meer <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\"> . groeten nr 6');
