INSERT INTO `gastenboek` VALUES ('105', 'NO. 9 (accepteren kloks)', '2003-05-20 22:11:14', 'Och god, ons spuitelf staat ook weer in het gastenboek. We zullen die honderd bijnamen van Stijn vrijdag wel \'s doorspreken onder het genot van veel  <IMG SRC=\"emoticons/beer_yum.gif\"> , witbier wel te verstaan, meegebracht door Kloksie boy. Tot dan!  <IMG SRC=\"emoticons/thumbs_up.gif\"> ');
