INSERT INTO `gastenboek` VALUES ('600', 'Maarten', '2004-01-06 15:58:16', 'Dag jongens!\r\n\r\nGroeten vanuit een zonnig Perugia! Voor de geinteresseerden: vanaf volgende week kunnen jullie kijken op www.goingperugia.com voor nu nog ff op www.hpwish.com voor verhalen en foto\'s van mn verblijf. Heel veel suc6 dit weekend in de Kemmer, haal de winst binne dan smaakt het bier daarna des te lekkerder!  <IMG SRC=\"emoticons/thumbs_up.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\"> Ciao, Maarten!  ');
