INSERT INTO `gastenboek` VALUES ('854', 'Bart vd Ven', '2004-04-23 15:05:08', 'Hallo allemaal,\r\n\r\nMHC Sint Oedenrode organiseert dit jaar van 28 t/m 31 mei alweer voor de 18e keer een internationaal hockeytoernooi, de laatste 2 jaar onder de naam Est Bibendum. Dit jaar is het thema carnaval en we gaan er weer 1 groot feest van maken, inclusief polonaise en dweilorkest.\r\nVorig jaar was het volle bak en ook voor dit jaar zijn de eerste inschrijvingen al binnen. Surf snel even naar onze site en schrijf je in, dan zien we je met Pinksteren!!\r\n\r\nGroet,\r\n\r\nOrganisatie Est Bibendum\r\nwww.mhcsint-oedenrode.nl/estbibendum  <IMG SRC=\"emoticons/beer_yum.gif\"> ');
