INSERT INTO `gastenboek` VALUES ('2479', 'H.C. Drunen', '2006-04-24 16:09:33', 'Dit zijn wel erg radicale teksten richting Liempde. \r\nIk hoor dat er een F.A.L. (Front Anti Liempde) is opgericht. Wordt steeds gekker <IMG SRC=\"emoticons/tounge_smile.gif\"> !\r\nHelaas, sluiten wij ons daar niet bij aan. Aangezien wij naar alle waarschijnlijkheid volgend jaar uitkomen in de 2e klasse C, zijn wij geen toegevoegde waarde aan dit front. Neemt niet weg dat wij de geleden uitnederlaag (3-1)recht zetten op 28 mei. Hockeer heeft ons 2 vaten bier beloofd bij winst... <IMG SRC=\"emoticons/tounge_smile.gif\"> \r\n\r\n\r\n <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\">  <IMG SRC=\"emoticons/beer_yum.gif\"> ');
