INSERT INTO `nieuws` VALUES ('12', '2003-04-22', 'Hockeyheren vriendschappelijk tegen Best', 'Aanstaande vrijdag 25 april spelen de Oirschotse Hockeyheren een vriendschappelijke wedstrijd uit tegen Best. Na een week rust in de competitie werken de heren via deze weg toe naar de laatste rondes uit de competitie. Met de vierde plek in het vizier wordt de draad volgende week weer opgepakt met de uit wedstrijd tegen HOD. ', '0');
